content.c_CSS:
.Hero-imageCaption {
text-shadow: 0 0 10px #010101;
}

/* バックグラウンドカラーを濃いめに */
.Consultants-wrapper {
    background-color: #f5f6f7;
}
.Locator-wrapper {
    background-color: #f5f6f7;
}
.Teaser--article.Teaser--articleTop {
    background-color: #f5f6f7;
}
.InfoItem {
    background-color: #f5f6f7;
}

/* サブタイトルのラップ */
.Consultants-subtitle {
white-space: pre-wrap;
}
.Locator-subtitle {
white-space: pre-wrap;
}
.Info-subtitle {
white-space: pre-wrap;
}


/* Information セクションの画像をオリジナルのままの比率で表示 */
.InfoItem-image {
    height: unset;
}

/* アコーディオンタイトルのセンタリング */
.FAQ-questionText {
 margin: auto;
font-size: 1.1rem;
}

/* FAQ のリンクの色 */
.Consultants .FAQ--ace .FAQ-answerText a {
color: #3498db;

}

/* スムーススクロール */
html { 
scroll-behavior: smooth;
}

/* locator を hidden */
#locator {
display: none;
}

/* 
  下線部カラー：水色
  下線部の太さ：細目
*/
.OnlineConsultation-subtitle,
.Consultants-subtitle,
.Articles-subtitle,
.Info-subtitle 
{
    text-decoration-color: skyblue;
    text-decoration-thickness: 0.15rem;
}

