# --- Free Section
#
#.JTB-gtop_areapurpose_tabs {
#  width: 1138px;
#  margin: 20px auto 0;
#}

.tab_content {
  clear: both;
  overflow: hidden;
}

.JTB-gtop_areapurpose_tabs ul.destination_linkList {
  display: flex;
  justify-content: flex-start;
  flex-wrap: wrap;
  flex-direction: row;
  align-items: center;
  padding-top: 24px;
  padding-bottom: 17px;
  gap: 16px;
}

ul {
  list-style: none;
  margin-block-start: 1em;
  margin-block-end: 1em;
  margin-inline-start: 0px;
  margin-inline-end: 0px;
  padding-inline-start: 40px;
  unicode-bidi: isolate;
}

.JTB-gtop_areapurpose_tabs .destination_linkList li {
  width: calc(100% / 4 - 20px);
  height: 80px;
  background: #ffffff;
  display: list-item;
  text-align: -webkit-match-parent;
  unicode-bidi: isolate;
}

.JTB-gtop_areapurpose_tabs .destination_linkList li a {
  display: flex;
  padding: 0 10px;
  color: #3498DB;
  border: 1px solid #bcbdc2;
  border-radius: 4px;
  text-align: center;
  justify-content: center;
  align-items: center;
  font-size: 16px;
  font-weight: 700;
  line-height: 150%;
  height: 100%;
  white-space: pre-wrap;

}

.JTB-gtop_areapurpose_tabs .destination_linkList li a:link {
    color: #3498DB;
    text-decoration: none;
}

.JTB-gtop_areapurpose_tabs .destination_linkList li a::after {
  content: "";
  display: inline-block;
  background-image: url(data:image/svg+xml;base64,);
  background-repeat: no-repeat;
  width: 8.5px;
  height: 16px;
  background-size: contain;
  vertical-align: middle;
  margin-left: 1rem;
}

# section3
.RegionList {
    margin-left: auto;
    margin-right: auto;
    padding-left: 1rem;
    padding-right: 1rem;
    width: 100%;
    padding-inline: 0;
    margin-bottom: 1rem;
}

.RegionList-contents {
    padding: 2rem 3rem;
    position: relative;
}

.Region {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    margin-bottom: .625rem;
}

.Region-name {
    font-size: 16px;
    line-height: 1.57;
    font-weight: 700;
    color: #fff;
    background-color: var(--region-color);
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-pack: center;
    -ms-flex-pack: center;
    justify-content: center;
    -webkit-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    padding: .375rem .625rem;
    min-width: 10rem;
#    white-space: pre-line;
    text-align: center;
}

.Region-prefectures {
    font-size: 16px;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    width: 100%;
    -webkit-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    -ms-flex-wrap: wrap;
    flex-wrap: wrap;
    background-color: #fff;
    padding: .25rem;
    gap: 1rem;
    padding-left: 1rem;
}

.Region-prefectureLink {
    font-size: .875rem;
    line-height: 1.57;
    text-decoration: none;
    color: #3498DB;
    -webkit-transition: border-color 100ms ease-in-out;
    transition: border-color 100ms ease-in-out;
}

a.Region-prefectureLink:after {
content: "";
    display: inline-block;
    background-image: url(data:image/svg+xml;base64,);
    background-repeat: no-repeat;
    width: 8.5px;
    height: 16px;
    background-size: contain;
    vertical-align: middle;
    margin-left: 0.5rem;
}
.Region-prefectureLink.Region-prefectureLink--disabled {
    color: lightgray;
}

.TopOverseasExpert-wrapper {
padding-bottom:56px;
}

#onlineconsultation {
display:none;
}

.pc { display: block !important;  border: 0px;}
.sp { display: none !important; }

